                                function loadDataForDate(date) {

                                    var d = date.getDate();
                                    var m = date.getMonth() + 1;
                                    var y = date.getFullYear();
                                    var parsableDate = y + '-' + (m <= 9 ? '0' + m : m) + '-' + (d <= 9 ? '0' + d : d);

                                    history.pushState({}, '', changeGetParameterInUrl(window.location.href, "date", parsableDate));

