                            </style>
                            <script type="text/javascript">
                                function renderGraph(myData, myLabelData, myAxisXlabel, myAxisYlabels, myAxisYnames) {
                                    var ctx = document.getElementById("bigchart").getContext('2d');
                                    var axisLimits = getAxesLimitsForData(myData, 8, 1.15);

                                    var axisTemplates = [
                                        {
                                            borderColor: 'rgba(255, 51, 0, 1)',
                                            borderWidth: 2,
                                            backgroundColor: 'rgba(255, 255, 255, 1)',
                                            fill: false,
                                            type: 'line',
                                            lineTension: 0,
                                        },
                                        {
                                            backgroundColor: 'rgba(0, 51, 153, 1)',
                                            borderWidth: 0,
                                        },
                                        {
                                            borderColor: 'rgba(255, 51, 0, 1)',
                                            borderWidth: 2,
                                            backgroundColor: 'rgba(255, 255, 255, 1)',
                                            fill: false,
                                            type: 'line',
                                            lineTension: 0,
                                        },
                                    ];

                                    var myChart = new Chart(ctx, {
                                        type: 'bar',
                                        data: {
                                            labels: myLabelData,
                                            datasets: [],
                                        },
                                        options: {
                                            legendCallback: function (chart) {
                                                var text = [];
                                                text.push('<ul class="' + chart.id + '-legend" style="list-style-type: none; padding-left: 0px;">');
                                                for (var i = 0; i < chart.data.datasets.length; i++) {
                                                    var axis = chart.data.datasets[i];
                                                    text.push('<li data-datasetIndex="' + chart.legend.legendItems[i].datasetIndex + '" onclick="updateDataset(event, ' + '\'' + chart.legend.legendItems[i].datasetIndex + '\'' + ')" class="legend-li">');
                                                    if (axis['type'] == "line") {
                                                        text.push('<div style="width: 40px; height: 15px; margin-right: 5px;border:1px solid #CCCCCC;display: inline-block;">\n\
                                    <div style="height: 3px; width: 12px; background: ' + axis['borderColor'] + '; float: left; margin: 5px 0px 0px 3px;"></div>\n\
    <div style="border-radius: 50px; border: 2px solid ' + axis['borderColor'] + '; background: ' + axis['backgroundColor'] + '; height: 8px; width: 8px;margin: 3px 0px 0px 0px;float: left;"></div>\n\
    <div style="height: 3px; width: 12px; background: ' + axis['borderColor'] + ';float: right;margin: 5px 3px 0px 0px;"></div>\n\
    \n\
    \n\</div>');
                                                    } else {
                                                        text.push('<div style="display: inline-block; width: 40px; height: 15px; margin-right: 5px; background-color:' + axis['backgroundColor'] + '"></div>');
                                                    }
                                                    if (chart.data.datasets[i].label) {
                                                        text.push('<span class="axis-name">' + chart.data.datasets[i].label + '</span>');
                                                    }
                                                    text.push('</li>');
                                                }
                                                text.push('</ul>');
                                                return text.join("");
                                            },
                                            legend: {
                                                display: false,
                                            },
                                            tooltips: {
                                                enabled: false,
                                                mode: 'index',
                                                intersect: true,
                                                custom: function (tooltip) {
                                                    var chart = this._chart;
                                                    var tooltipEl = document.getElementById('chartjs-tooltip');
                                                    if (!tooltipEl) {
                                                        tooltipEl = document.createElement('div');
                                                        tooltipEl.id = 'chartjs-tooltip';
                                                        tooltipEl.innerHTML = "<table></table>"
                                                        this._chart.canvas.parentNode.appendChild(tooltipEl);
                                                    }

                                                    // Hide if no tooltip
                                                    if (tooltip.opacity === 0) {
                                                        tooltipEl.style.opacity = 0;
                                                        return;
                                                    }

                                                    // Set caret Position
                                                    tooltipEl.classList.remove('above', 'below', 'no-transform');
                                                    if (tooltip.yAlign) {
                                                        tooltipEl.classList.add(tooltip.yAlign);
                                                    } else {
                                                        tooltipEl.classList.add('no-transform');
                                                    }

                                                    function getBody(bodyItem) {
                                                        return bodyItem.lines;
                                                    }

                                                    // Set Text

                                                    if (tooltip.body) {
                                                        var titleLines = tooltip.title || [];
                                                        var bodyLines = tooltip.body.map(getBody);
                                                        var innerHtml = '<thead>';
                                                        titleLines.forEach(function (title) {
                                                            innerHtml += '<tr><th>' + title + '</th></tr>';
                                                        });
                                                        innerHtml += '</thead><tbody>';

                                                        var correctDatasetIds = [];
                                                        chart.legend.legendItems.forEach(function (legendItem, legendIndex) {
                                                            if (legendItem.hidden == false) {
                                                                correctDatasetIds.push(legendIndex);
                                                            }
                                                        });

                                                        bodyLines.forEach(function (body, indexInArray) {
                                                            var i = indexInArray;
                                                            var datasetI = correctDatasetIds[indexInArray];

                                                            var colors = tooltip.labelColors[i];
                                                            var style = 'background:' + colors.backgroundColor;
                                                            style += '; border-color:' + colors.borderColor;
                                                            style += '; border-width: 2px';
                                                            var axis = chart.data.datasets[datasetI];
                                                            if (axis['type'] == "line") {
                                                                var span = '<div style="width: 40px; height: 15px; margin-right: 5px;border:1px solid #CCCCCC;display: inline-block;background: white;"><div style="height: 3px; width: 12px; background: ' + axis['borderColor'] + '; float: left; margin: 5px 0px 0px 3px;"></div><div style="border-radius: 50px; border: 2px solid ' + axis['borderColor'] + '; background: ' + axis['backgroundColor'] + '; height: 8px; width: 8px;margin: 3px 0px 0px 0px;float: left;"></div><div style="height: 3px; width: 12px; background: ' + axis['borderColor'] + ';float: right;margin: 5px 3px 0px 0px;"></div></div>';
                                                            } else {
                                                                var span = '<div style="width: 40px; height: 15px; margin-right: 5px;border:1px solid #CCCCCC;display: inline-block;background-color:' + axis['backgroundColor'] + ';"></div>';
                                                            }

                                                            if (axis['yAxisID'] == 'second-y-axis') {
                                                                var decimalsLength = 2;
                                                            } else {
                                                                var decimalsLength = 1;
                                                            }


                                                            innerHtml += '<tr><td>' + span + axis['label'] + ':&nbsp;&nbsp;' + formatNumber(body, decimalsLength, ',', ' ') + '</td></tr>';
                                                        });
                                                        innerHtml += '</tbody>';
                                                        var tableRoot = tooltipEl.querySelector('table');
                                                        tableRoot.style.border = "0px";
                                                        tableRoot.innerHTML = innerHtml;
                                                    }

                                                    var positionY = this._chart.canvas.offsetTop;
                                                    var positionX = this._chart.canvas.offsetLeft;
                                                    // Display, position, and set styles for font
                                                    tooltipEl.style.opacity = 1;
                                                    tooltipEl.style.left = positionX + tooltip.caretX + 'px';
                                                    tooltipEl.style.top = positionY + tooltip.caretY + 'px';
                                                    tooltipEl.style.padding = tooltip.yPadding + 'px ' + tooltip.xPadding + 'px';
                                                    tooltipEl.style.borderRadius = '8px';
                                                },
                                                callbacks: {
                                                    title: function (tooltipItem, data) {
                                                        return tooltipItem[0]['xLabel'] + ". hodina";
                                                    }
                                                    ,
                                                    label: function (tooltipItem, data) {


                                                        return tooltipItem.yLabel;
                                                    }
                                                }
                                            },
                                            scales: {
                                                xAxes: [{
                                                        ticks: {
                                                            maxRotation: 0,
                                                            autoSkip: false,
                                                        },
                                                        scaleLabel: {
                                                            display: true,
                                                            labelString: myAxisXlabel,
                                                        },
                                                        gridLines: {
                                                            display: false,
                                                        }
                                                    }],
                                                yAxes: [{
                                                        id: 'first-y-axis',
                                                        type: 'linear',
                                                        position: 'right',
                                                        ticks: {
                                                            suggestedMin: axisLimits[0]["suggestedMin"],
                                                            suggestedMax: axisLimits[0]["suggestedMax"],
                                                            stepSize: axisLimits[0]["stepSize"],
                                                            callback: function (value, index, values) {
                                                                return formatNumber(Math.floor(value), 0, ',', ' ');
                                                            }
                                                        },
                                                        scaleLabel: {
                                                            display: true,
                                                            labelString: myAxisYlabels[1]
                                                        }
                                                    }, {
                                                        id: 'second-y-axis',
                                                        type: 'linear',
                                                        position: 'left',
                                                        ticks: {
                                                            suggestedMin: axisLimits[1]["suggestedMin"],
                                                            suggestedMax: axisLimits[1]["suggestedMax"],
                                                            stepSize: axisLimits[1]["stepSize"],
                                                            //beginAtZero: true,
                                                            callback: function (value, index, values) {
                                                                var space = "";
                                                                if (value >= 0) {
                                                                    space = " ";
                                                                }
                                                                return space + formatNumber(value, 0, ',', ' ');
                                                            }
                                                        },
                                                        scaleLabel: {
                                                            display: true,
                                                            labelString: myAxisYlabels[0]
                                                        },
                                                        gridLines: {
                                                            display: false,
                                                        }
                                                    }
                                                ]
                                            },
                                            pan: {
                                                enabled: true,
                                                mode: 'xy',
                                            },
                                            zoom: {
                                                enabled: true,
                                                mode: 'xy',
                                            },
                                        }
                                    });



                                    if (myData.length == 3) {
                                        var myAxisIds = ['second-y-axis', 'first-y-axis', 'first-y-axis'];
                                        var tempAxisTemplate = axisTemplates[1];
                                        axisTemplates[1] = axisTemplates[0];
                                        axisTemplates[0] = tempAxisTemplate;

                                        axisTemplates[1].type = "bar";
                                        axisTemplates[1].backgroundColor = axisTemplates[1].borderColor = 'rgba(230, 199, 21, 1)';


                                        myData = myData.reverse();
                                        axisTemplates = axisTemplates.reverse();
                                        myAxisYnames = myAxisYnames.reverse();

                                        myChart.options.scales.yAxes[1].ticks.suggestedMin = 0;
                                        myChart.options.scales.yAxes[1].ticks.suggestedMax = 80;
                                        myChart.options.scales.yAxes[1].ticks.stepSize = 10;


                                    } else {
                                        var myAxisIds = ['second-y-axis', 'first-y-axis'];

                                        var tempSwap01 = myData[1];
                                        myData[1] = myData[0];
                                        myData[0] = tempSwap01;
                                        tempSwap01 = myAxisYnames[1];
                                        myAxisYnames[1] = myAxisYnames[0];
                                        myAxisYnames[0] = tempSwap01;
                                    }


                                    myData.forEach(function (axisObject, axisIndex) {

                                        var tempObject = axisTemplates[axisIndex];
                                        tempObject['data'] = axisObject;
                                        tempObject['yAxisID'] = myAxisIds[axisIndex];
                                        tempObject['label'] = myAxisYnames[axisIndex];
                                        myChart.data.datasets.push(tempObject);
                                    });

                                    myChart.update();

                                    updateDataset = function (e, datasetIndex) {
                                        var index = datasetIndex;
                                        var ci = myChart;
                                        var meta = ci.getDatasetMeta(index);
                                        meta.hidden = meta.hidden === null ? !ci.data.datasets[index].hidden : null;
                                        var axisLabel = $("#chartjs-legend>ul>li[data-datasetIndex=" + datasetIndex + "]>.axis-name");
                                        if (!meta.hidden) {
                                            axisLabel.css('text-decoration', 'none');
                                        } else {
                                            axisLabel.css('text-decoration', 'line-through');
                                        }

                                        ci.update();
                                    };
                                    $("#chartjs-legend").html(myChart.generateLegend());
                                }
            </script>

